import React, { useEffect, useRef } from "react";
export default function Hangman() {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        const radius = canvas.height / 4;
        const startAngle = 0;
        const endAngle = Math.PI * 2;
        const lineWidth = 2;
        const color = 'white';
        const lineHeight = 50;

        let currentAngle = startAngle;
        let currentHeight = 0;
        let requestIdLine = null;
        let requestIdCircle = null;
        const drawFrame = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.beginPath();
            ctx.arc(canvas.width / 2, canvas.height / 2, radius, startAngle, currentAngle);
            ctx.lineWidth = lineWidth;
            ctx.strokeStyle = color;
            ctx.stroke();

            currentAngle += 0.1;
            if (currentAngle > endAngle) {
                cancelAnimationFrame(requestIdCircle);
                requestIdCircle = null; 
                return;
            }

            requestIdCircle = requestAnimationFrame(drawFrame);
        };

        const drawLine = () => {
            ctx.beginPath();
            ctx.moveTo(canvas.width / 2, canvas.height + radius);
            ctx.lineTo(canvas.width / 2, canvas.height + currentHeight);
            ctx.lineWidth = lineWidth;
            ctx.strokeStyle = color;
            ctx.stroke();
            currentHeight += 2;
            if (currentHeight > lineHeight) {
                cancelAnimationFrame(requestIdLine);
                requestIdLine = null; 
                return;
            }
            requestIdLine = requestAnimationFrame(drawLine);
        }

        drawFrame();
        drawLine();
    }, []);

    return (
        <div className='viewContainer'>
            <div className='headerContainer' >
                <canvas ref={canvasRef} width={400} height={400} />;
                <h2 className='header'>Hangman</h2>

            </div>
            <h2 className='header'>Hangman</h2>
            <h2 className='header'>Hangman</h2>
            <h2 className='header'>Hangman</h2>
            <h2 className='header'>Hangman</h2>
        </div>
    )
}
