import React, { useState } from 'react';
import './TicTacToe.css';

const TicTacToe = () => {
    const [board, setBoard] = useState(Array(9).fill(null));
    const [isXturn, setIsXTurn] = useState(null);

    const renderSquare = (index) => (
        <div key={index} className="square" >
            {board[index]}
        </div>
    );

    const handleClick = (index) => {
        if (board[index] || calculateWinner(board)) {
          return;
        }

        const newBoard = board.slice();
        newBoard[index] = isXNext ? 'X' : 'O';

        setBoard(newBoard);
        setIsXNext(!isXNext);
      };

    const winner = calculateWinner(board);
    const status = winner
      ? `Winner: ${winner}`
      : `Next player: ${isXturn ? 'X' : 'O'}`;

    const calculateWinner = (squares) => {
        const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6],
        ];

        for (const [a, b, c] of lines) {
          if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
          }
        }

        return null;
      };

    return (
        <div className='viewContainer'>
            <div className='headerContainer'>
                <div className='logo'>
                    <div className='vertical'>
                        <div className="vertical-line"></div>
                        <div className="vertical-line"></div>
                    </div>
                    <div className="horizontal">
                        <div className="horizontal-line"></div>
                        <div className="horizontal-line"></div>
                    </div>
                </div>
                <h2 className='header'>Tic Tac Toe</h2>
            </div>

            <p className='description'>Please select a button to start first.</p>
            <div className='buttonContainer'>
                <button className={`XOButton ${isXturn == true ? 'selected' : ''}`}
                    onClick={() => setIsXTurn(true)}
                > X
                </button>
                <button className={`XOButton ${isXturn == false ? 'selected' : ''}`}
                    onClick={() => setIsXTurn(false)}
                > O
                </button>
            </div>
            <div>
                <p className="status">{status}</p>
                <div className="board">
                    {Array.from({ length: 3 }).map((_, row) => (
                        <div key={row} className="board-row">
                            {Array.from({ length: 3 }).map((_, col) => {
                                const index = row * 3 + col;
                                return renderSquare(index);
                            })}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )


}

export default TicTacToe;
